.fd-modal-container {
    position: fixed;
    top: 0;
    bottom: 0;
    right: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

:host {
    display: block;
    overflow: auto;
    flex-grow: 1;
}

:host {
    display: block;
    border-top: 1px solid #eeeeef;
}

